from flask import Flask,render_template

app = Flask(__name__)

@app.route("/")
@app.route("/dashboard")
def hello():
    return render_template("newHomePage.html")

@app.route("/profile")
def profile():
    return render_template("newAlertPage.html",image= "data:image/jpeg;base64,",animalName = "Elephant")

if __name__=="__main__":
    app.run(debug=True)